package logica.variaveis;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//int idade;
		//idade = 20;
		
		int idade = 20;
		double nota = 8.6;
		boolean ligado = true; // boolean só recebe true ou false.
		boolean ativo = false;
		String nome = "Joao";
		
		System.out.println(nota);
		
		System.out.println(nome + " -> " + nota);
		
		//int 2x;
		int x2;
		
		int numeroDePessoas = 10; // variaveis sempre com a letra inicial minuscula
		
		// "Camel case" - > numeroDePessoas
		
		
		// variavel do tipo "generico" -> var, o computador compila e e determina o tipo pelo contexto atribuido.
		
		var x = 5; 
		var y = "abc";
		var z = 2.2;
		var w = true;
	}

}
